#include <iostream>
using namespace std;

int main() {
    const int N = 20; // Number of Fibonacci numbers to print
    long long fib[N]; // Array to store Fibonacci numbers
    fib[0] = 0;       // Initial value
    fib[1] = 1;       // Initial value

    // Generate Fibonacci sequence
    for (int i = 2; i < N; i++) {
        fib[i] = fib[i - 1] + fib[i - 2];
    }

    // Print the sequence
    cout << "First 20 Fibonacci numbers:" << endl;
    for (int i = 0; i < N; i++) {
        cout << fib[i] << " ";
    }
    cout << endl;

    return 0;
}